sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }	
   //Type appropriate comment here, and begin script below
   var incident_ref = g_form.getValue('related_incident');

 //Call script include
 var ga = new GlideAjax('x_1393996_scriptin.Update_Incident_Short_Description');   //Scriptinclude
 ga.addParam('sysparm_name', 'getIncidentDetails'); //Method
 ga.addParam('sys_id',incident_ref); //Parameters
 ga.getXMLAnswer(getResponse);

 function getResponse(response){
  console.log(response);
  var res = JSON.parse(response);
  console.log(res);
  g_form.setValue('incident_short_description',res.short_description);
 }

}
