sys_script_include.script:
var CreateRecord_Incident = Class.create();
CreateRecord_Incident.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	createNewRecord:function(name,description,related_incident,incident_short_description){
		var newRecord = new GlideRecord('x_1393996_scriptin_cconnectnow_practice');

		gs.addInfoMessage("Client Script");

		var glideRecord = new GlideRecord('sys_user');	
		glideRecord.addQuery("name","=",name);
		glideRecord.query();

		while(glideRecord.next())
		{
			gs.addInfoMessage("name : "+glideRecord.name);
			newRecord.setDisplayValue("name",glideRecord.name);
			newRecord.setDisplayValue("email",glideRecord.email);
		}

		gs.addInfoMessage("Before Descriptino");
		newRecord.setValue("description","Above Details are correct");
		newRecord.setValue("related_incident",related_incident);
		newRecord.setValue("incident_short_description",incident_short_description);

		var newRecordSysId = newRecord.insert();
		gs.addInfoMessage("After Insert");
		if (newRecordSysId) {
            gs.info('New record created in x_1393996_scriptin_cconnectnow_practice with sys_id: ' + newRecordSysId);
            return newRecordSysId;
        } else {
            gs.error('Failed to create new record in x_1393996_scriptin_cconnectnow_practice');
            return null;
        }
	},

    type: 'CreateRecord_Incident'
});
