sys_script_include.script:
var Update_Incident_Short_Description = Class.create();
Update_Incident_Short_Description.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	getIncidentDetails:function(){

		var incident_reference = this.getParameter('sys_id');

		var incident = new GlideRecord('incident');
		obj = {};

		if(incident.get(incident_reference))
		{
			obj.short_description = incident.getValue("short_description");
		}

		return JSON.stringify(obj);
	},
    type: 'Update_Incident_Short_Description'
});
